class TranspositionTable:
    def __init__(self):
        self.table = {}

    def get(self, zobrist_hash, depth):
        if zobrist_hash in self.table:
            entry = self.table[zobrist_hash]
            tt_score, tt_depth, tt_flag, tt_move = entry
            if tt_depth >= depth:
                return entry
        return None

    def store(self, zobrist_hash, score, depth, flag, move):
        self.table[zobrist_hash] = (score, depth, flag, move)