import random

# Zobrist hashing keys
ZOBRIST_KEYS = {
    'pieces': {},  # piece_type -> [64 keys for each square]
    'castling': {},  # castling right -> key
    'enpassant': {},  # en passant file -> key
    'side': None  # key for side to move
}


def init_zobrist_keys():
    # Initialize piece keys for each piece type at each square
    pieces = ["wp", "wR", "wN", "wB", "wQ", "wK", "bp", "bR", "bN", "bB", "bQ", "bK"]
    for piece in pieces:
        ZOBRIST_KEYS['pieces'][piece] = [random.getrandbits(64) for _ in range(64)]

    # Initialize castling keys
    ZOBRIST_KEYS['castling']['wks'] = random.getrandbits(64)
    ZOBRIST_KEYS['castling']['wqs'] = random.getrandbits(64)
    ZOBRIST_KEYS['castling']['bks'] = random.getrandbits(64)
    ZOBRIST_KEYS['castling']['bqs'] = random.getrandbits(64)

    # Initialize en passant file keys (files a-h)
    for file in range(8):
        ZOBRIST_KEYS['enpassant'][file] = random.getrandbits(64)

    # Initialize side to move key
    ZOBRIST_KEYS['side'] = random.getrandbits(64)